mapper.select:
select

from tb_cate


        and module_id = #{moduleId,jdbcType=VARCHAR}


        and cate_name = #{cateName,jdbcType=VARCHAR}


        and cate_url = #{cateUrl,jdbcType=VARCHAR}


        and cate_desc = #{cateDesc,jdbcType=VARCHAR}


        and cate_icon = #{cateIcon,jdbcType=VARCHAR}


        and is_filled = #{isFilled,jdbcType=INTEGER}


        and update_time = #{updateTime,jdbcType=VARCHAR}


        and cate_creater = #{cateCreater,jdbcType=VARCHAR}


        and create_time = #{createTime,jdbcType=VARCHAR}


        and cate_updater = #{cateUpdater,jdbcType=VARCHAR}


        and cate_key = #{cateKey,jdbcType=VARCHAR}

select count(1)
from tb_cate
where cate_name = #{cateName}

select count(1)
from tb_cate
where cate_key = #{cateKey}

select cate_id cateId,cate_name cateName,cate_key cateKey
from tb_cate
where module_id = #{moduleId}
